import React, { useState } from 'react'

import SimpleHooks from './components/SimpleHooks';
import HooksWithSingleState from './components/HooksWithSingleState';
import HooksWithMultipleState from './components/HooksWithMultipleState';

const Welcome = props  => {

    const [selectedComp, setSelectedComp] = useState(0)

    const titleList = [ 
        {title: "Simple Hooks", comp: "SimpleHooks"},
        {title: "Hooks with Multiple State", comp: "HooksWithMultipleState"},
        {title: "Hooks with Single State", comp: "HooksWithSingleState"},
        "Rules of Hooks",
        "Sending Data via Http",
        "The useEffect() Hook",
        "The useContext() Hook",
        "The useReducer() Hook",
        "The useReducer() vs useState()",
        "The useRef()",
        "Create a custom Hook"
    ]

    const onTitleClick = (e, comp) => {
        e.preventDefault()
        setSelectedComp(comp)
    }

    return (
        <React.Fragment>
            <div className="container-fluid">
                <div className="jumbotron">
                    <h2 className="text-center">Surfing with Hooks</h2>
                </div>
                <div className="row">
                    <div className="col-sm-3">
                        <label htmlFor="name">Title List:</label>
                        <ul className="list-group">
                            {   titleList.length === 0 &&
                                <li className="list-group-item">No Title Available</li>
                            }
                            {   titleList.map((item, index) => {
                                return (
                                    <li key={index} style={{backgroundColor: index === selectedIndex ? 'lightgray' : ''}} 
                                        className="list-group-item" onClick={e=>onTitleClick(e,item.comp)}>
                                        {item}
                                    </li>
                                )
                            })}
                        </ul>
                    </div>
                    <div className="col-sm-9">
                        {selectedIndex === 0 && <selectedComp title={titleList[selectedIndex].title}/> }
                        {selectedIndex === 1 && <selectedComp title={titleList[selectedIndex].title}/> }
                        {selectedIndex === 2 && <selectedComp title={titleList[selectedIndex].title}/> }
                    </div>
                </div>
            </div>
        </React.Fragment>
    )
}

export default Welcome
