import React, { useState } from 'react'
import Todo from './comps/Todo';

const Welcome = props  => {

    const [titleName, setTitleName] = useState('')
    const [titleList, setTitleList] = useState([])

    const onInputChange = (event) => {
        setTitleName(event.target.value)
    }

    const onAddClickButtonHandler = () => {
        setTitleList(titleList.concat(titleName))
        setTitleName('')
    }

    return (
        <React.Fragment>
            <div className="container-fluid">
                <div className="jumbotron">
                    <h2 className="text-center">Surfing with Hooks</h2>
                </div>
                <div className="row">
                    <div className="col-sm-3">
                        <label htmlFor="name">Title List:</label>
                        <ul className="list-group">
                            {   titleList.length === 0 &&
                                <li className="list-group-item">No Title Available</li>
                            }
                            {   titleList.map((item, index) => {
                                return (
                                    <li key={index} className="list-group-item">
                                        {item}
                                    </li>
                                )
                            })}
                        </ul>
                    </div>
                    <div className="col-sm-9">
                        <Todo />
                    </div>
                </div>
            </div>
        </React.Fragment>
    )
}

export default Welcome
