import React, { useState } from 'react'

import SimpleHooks from './components/SimpleHooks';
import HooksWithSingleState from './components/HooksWithSingleState';
import HooksWithMultipleState from './components/HooksWithMultipleState';

const Welcome = props  => {

    const [selectedIndex, selectedComp, setSelectedComp] = useState(0, {title: '', comp: ''})

    const titleList = [ 
        {title: "Simple Hooks", comp: {SimpleHooks}},
        {title: "Hooks with Multiple State", comp: {HooksWithMultipleState}},
        {title: "Hooks with Single State", comp: {HooksWithSingleState}},
        // {title: "Rules of Hooks", comp: ""},
        // {title: "Sending Data via Http", comp: ""},
        // {title: "The useEffect() Hook", comp: ""},
        // {title: "The useContext() Hook", comp: ""},
        // {title: "The useReducer() Hook", comp: ""},
        // {title: "The useReducer() vs useState()", comp: ""},
        // {title: "The useRef()", comp: ""},
        // {title: "Create a custom Hook", comp: ""}
    ]

    const onTitleClick = (e, index, title, comp) => {
        e.preventDefault()
        setSelectedComp(index, {title: title, comp: comp})
    }

    return (
        <React.Fragment>
            <div className="container-fluid">
                <div className="jumbotron">
                    <h2 className="text-center">Surfing with Hooks</h2>
                </div>
                <div className="row">
                    <div className="col-sm-3">
                        <label htmlFor="name">Title List:</label>
                        <ul className="list-group">
                            {   titleList.length === 0 &&
                                <li className="list-group-item">No Title Available</li>
                            }
                            {   titleList.map((item, index) => {
                                return (
                                    <li key={index} style={{backgroundColor: index === selectedIndex ? 'lightgray' : ''}} 
                                        className="list-group-item" onClick={e=>onTitleClick(e, index, item.title, item.comp)}>
                                        {item.title}
                                    </li>
                                )
                            })}
                        </ul>
                    </div>
                    <div className="col-sm-9">
                        <selectedComp.comp title={selectedComp.title}/>
                    </div>
                </div>
            </div>
        </React.Fragment>
    )
}

export default Welcome
